interface ICommitInfo {
  sha: string;
  node_id: string;
  commit: {
    author: IAuthorOrCommitter;
    committer: IAuthorOrCommitter;
    message: string;
    tree: {
      sha: string;
      url: string;
    };
    url: string;
    comment_count: number;
    verification: {
      verified: boolean;
      reason: string;
      signature: any;
      payload: any;
    };
  };
  url: string;
  html_url: string;
  comments_url: string;
  author: IAuthorDetail;
  committer: ICommitterDetail;
  parents: Array<{
    sha: string;
    url: string;
    html_url: string;
  }>;
}

interface ICommitedFile {
  sha: string;
  filename: string;
  status: string;
  additions: number;
  deletions: number;
  changes: number;
  blob_url: string;
  raw_url: string;
  contents_url: string;
  patch: string;
}
interface ICommitDetails {
  sha: string;
  node_id: string;
  commit: {
    author: IAuthorOrCommitter;
    committer: IAuthorOrCommitter;
    message: string;
    tree: {
      sha: string;
      url: string;
    };
    url: string;
    comment_count: number;
    verification: {
      verified: boolean;
      reason: string;
      signature: any;
      payload: any;
    };
  };
  url: string;
  html_url: string;
  comments_url: string;
  author: IAuthorDetail;
  committer: ICommitterDetail;
  parents: Array<{
    sha: string;
    url: string;
    html_url: string;
  }>;
  stats: {
    total: number;
    additions: number;
    deletions: number;
  };
  files: Array<ICommitedFile>;
}
